import React, {Link} from "react";
import "./Modal.css";
import Album from "../pages/album/Album";
import siteData from "../../context/siteData";
const Modal = (props) => {
  const url = siteData[0].releases[0].url;
  const albumTracks = siteData[1].album.tracks.map((track) => (
    <li>{track.trackName}</li>
  ));
  const confirmHandler = () => {
    props.onConfirm();
  };

  const cancelHandler = () => {
    props.onCancel();
  };
  return (
    <div className="modal">
      <ul>
        {albumTracks}
      </ul>
      <button  onClick={cancelHandler}>
        <Link to={url} key={index}>Visit Album</Link>
      </button>
      <button  onClick={confirmHandler}>
        Back to Library
      </button>
    </div>
  );
};

export default Modal;
