import React, {useState} from "react";

import classes from "../../ui/UI.module.css";
import siteData from "../../../context/siteData";
import Card from "../../ui/Card"
import Album from "../album/Album"
import Modal from "../../ui/Modal"
import Backdrop from "../../ui/Backdrop"
import "./Library.css"
const Library = () => {
   const [modal, setModal] = useState(false);
   const deleteHandler = () => {
     setModal(true);
   };
   const closeModalHandler = () => {
     setModal(false);
   };
    const getAlbum = siteData[0].releases.map((album, index) => (

        <li key={index}>{album.albumName}   <div className="actions">
            <button className="btn" onClick={deleteHandler}>
              Delete
            </button>
          </div>
          {modal && (
            <Modal onCancel={closeModalHandler} onConfirm={closeModalHandler}>
              <Album />
            </Modal>
          )}
          {modal && <Backdrop onClick={closeModalHandler} />}</li>

    ));
console.log(getAlbum)


  return (
    <>
      <div className="library_view">
        <ul className="album_view">
          {getAlbum}

        </ul>
      </div>
    </>
  );
};

export default Library;
